mapper.select:
select email, nickname
from member
where phone_number=#{phone_number}

mapper.update:
update member


		, pwd = #{pwd}


		, nickname = #{nickname}


		, name = #{name}


		, phone_number = #{phone_number}


where member_id = member_id

mapper.select:
select count(*)
from member
where email=#{email}

select count(*)
from member
where nickname=#{nickname}

select member_id, email, nickname, name, phone_number, profile, rdate, rating
from member
where email=#{email} and pwd=#{pwd}
